import BraintreeAmericanExpress
import BraintreeApplePay
import BraintreeCard
import BraintreeCore
import BraintreePaymentFlow
import BraintreePayPal
import BraintreeThreeDSecure
import BraintreeUnionPay
import BraintreeVenmo
import PayPalDataCollector

import CardinalMobile

class ViewController: UIViewController, BTThreeDSecureRequestDelegate {

    var paymentFlowDriver: BTPaymentFlowDriver?
    var cardClient: BTCardClient?

    override func viewDidLoad() {

        let apiClient = BTAPIClient(authorization: "")!
        self.paymentFlowDriver = BTPaymentFlowDriver(apiClient: apiClient)
        self.cardClient = BTCardClient(apiClient: apiClient)



        let card = BTCard(number: "4111111111111111", expirationMonth: "12", expirationYear: "23", cvv: "123")

        self.cardClient?.tokenizeCard(card, completion: { (cardNonce, error) in

            let request = BTThreeDSecureRequest()
            request.amount = NSDecimalNumber(string: "10.32")
            request.nonce = cardNonce!.nonce
            request.versionRequested = .version2
            request.threeDSecureRequestDelegate = self

            self.paymentFlowDriver?.startPaymentFlow(request, completion: { (result, error) in

            })
        })
    }

    func onLookupComplete(_ request: BTThreeDSecureRequest, lookupResult result: BTThreeDSecureResult, next: @escaping () -> Void) {
        next()
    }
}
